import React, { useState } from 'react';
import { Users, Brain, FileText, Briefcase, Download, Moon, Sun, Type, Sparkles, MessageSquare, BarChart3, Zap, Target, TrendingUp, CheckCircle, AlertCircle, Lightbulb, Rocket, Star, Eye, Edit3, RefreshCw } from 'lucide-react';
import './App.css';

const ResumeBuilder = () => {
  const [currentPage, setCurrentPage] = useState('builder');
  const [darkMode, setDarkMode] = useState(false);
  const [dyslexiaMode, setDyslexiaMode] = useState(false);
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const [selectedAI, setSelectedAI] = useState('resume');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [resumeScore, setResumeScore] = useState(82);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');

  // Resume data state
  const [resumeData, setResumeData] = useState({
    personal: {
      fullName: 'John Doe',
      jobTitle: 'Senior Software Engineer',
      email: 'john.doe@example.com',
      phone: '(555) 123-4567',
      location: 'San Francisco, CA',
      summary: 'Experienced software engineer with 5+ years of expertise in developing scalable web applications and leading development teams. Strong problem-solving skills and passion for clean code architecture.'
    },
    experience: [{
      company: 'Tech Innovations Inc.',
      position: 'Senior Software Engineer',
      startDate: '2020-01',
      endDate: '2023-08',
      description: '• Led a team of 5 developers in creating a new SaaS product\n• Improved system performance by 40% through optimization\n• Implemented CI/CD pipeline reducing deployment time by 60%'
    }],
    education: [{
      institution: 'Stanford University',
      degree: 'Master of Science in Computer Science',
      graduationDate: '2018-05',
      gpa: '3.9/4.0',
      achievements: '• Graduated with honors\n• Published research on machine learning algorithms\n• President of Computer Science Club'
    }],
    skills: ['JavaScript', 'React', 'Node.js', 'Python', 'SQL', 'AWS', 'Docker', 'Kubernetes', 'Team Leadership', 'Agile Methodologies'],
    jobDescription: 'We are looking for a Senior Software Engineer with 5+ years of experience in JavaScript, React, and Node.js. The ideal candidate will have experience leading teams and working with cloud technologies like AWS. Experience with DevOps practices and CI/CD pipelines is a plus.'
  });

  const [activeTab, setActiveTab] = useState('personal');

  // AI Specialists
  const aiSpecialists = {
    resume: {
      name: 'ResumeAI',
      icon: FileText,
      description: 'Resume optimization and formatting expert',
      color: 'from-blue-500 to-cyan-500'
    },
    career: {
      name: 'CareerAI',
      icon: TrendingUp,
      description: 'Career guidance and job matching specialist',
      color: 'from-purple-500 to-pink-500'
    },
    skills: {
      name: 'SkillsAI',
      icon: Brain,
      description: 'Skills assessment and development advisor',
      color: 'from-green-500 to-emerald-500'
    },
    interview: {
      name: 'InterviewAI',
      icon: MessageSquare,
      description: 'Interview preparation and practice coach',
      color: 'from-orange-500 to-red-500'
    },
    analytics: {
      name: 'AnalyticsAI',
      icon: BarChart3,
      description: 'Resume performance and market analysis',
      color: 'from-indigo-500 to-violet-500'
    }
  };

  // Simulate AI responses
  const getAIResponse = (specialist, message) => {
    const responses = {
      resume: [
        "I've analyzed your resume structure. Consider adding more quantifiable achievements in your experience section.",
        "Your skills section looks strong! I recommend grouping them by category: Technical Skills, Soft Skills, and Tools.",
        "The summary is compelling, but try to include 1-2 specific metrics that showcase your impact."
      ],
      career: [
        "Based on your background, you'd be a great fit for Senior Software Engineer roles at tech companies.",
        "I see opportunities in DevOps Engineering given your CI/CD experience. Want me to suggest companies?",
        "Your leadership experience opens doors to Engineering Manager positions. Should we explore that path?"
      ],
      skills: [
        "Your technical skills are well-rounded. Consider adding React Native and GraphQL to stay current.",
        "I notice strong backend skills. Adding microservices architecture would complement your profile.",
        "Your combination of technical and leadership skills is valuable. Highlight mentoring experience more."
      ],
      interview: [
        "Let's practice the STAR method for behavioral questions. Can you tell me about a challenging project?",
        "Common questions for your role: 'How do you handle technical debt?' and 'Describe your code review process.'",
        "I can simulate a technical interview. Would you like to start with system design or coding questions?"
      ],
      analytics: [
        "Your resume has an 82% match rate with Senior Engineer roles. Top missing keywords: 'microservices', 'scalability'.",
        "Industry analysis shows 40% higher demand for candidates with your skill set in the Bay Area.",
        "Your experience level puts you in the top 25% of candidates. Consider targeting senior roles at major tech companies."
      ]
    };

    return responses[specialist][Math.floor(Math.random() * responses[specialist].length)];
  };

  const sendMessage = () => {
    if (!currentMessage.trim()) return;

    const newMessage = {
      type: 'user',
      content: currentMessage,
      timestamp: new Date()
    };

    const aiResponse = {
      type: 'ai',
      specialist: selectedAI,
      content: getAIResponse(selectedAI, currentMessage),
      timestamp: new Date()
    };

    setChatMessages(prev => [...prev, newMessage, aiResponse]);
    setCurrentMessage('');
  };

  const exportResume = () => {
    const resumeContent = `
${resumeData.personal.fullName}
${resumeData.personal.jobTitle}
${resumeData.personal.email} | ${resumeData.personal.phone} | ${resumeData.personal.location}

SUMMARY
${resumeData.personal.summary}

SKILLS
${resumeData.skills.join(', ')}

EXPERIENCE
${resumeData.experience.map(exp => `
${exp.position} at ${exp.company}
${exp.startDate} - ${exp.endDate}
${exp.description}
`).join('\n')}

EDUCATION
${resumeData.education.map(edu => `
${edu.degree}
${edu.institution} - ${edu.graduationDate}
GPA: ${edu.gpa}
${edu.achievements}
`).join('\n')}
    `;

    const element = document.createElement('a');
    const file = new Blob([resumeContent], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `${resumeData.personal.fullName.replace(/\s+/g, '_')}_Resume.txt`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const addExperience = () => {
    const newExp = {
      company: '',
      position: '',
      startDate: '',
      endDate: '',
      description: ''
    };
    setResumeData(prev => ({
      ...prev,
      experience: [...prev.experience, newExp]
    }));
  };

  const addEducation = () => {
    const newEdu = {
      institution: '',
      degree: '',
      graduationDate: '',
      gpa: '',
      achievements: ''
    };
    setResumeData(prev => ({
      ...prev,
      education: [...prev.education, newEdu]
    }));
  };

  const applyTemplate = (templateId) => {
    const templates = {
      1: {
        personal: {
          ...resumeData.personal,
          summary: 'Results-driven professional with expertise in modern technologies and agile methodologies.'
        }
      },
      2: {
        personal: {
          ...resumeData.personal,
          summary: 'Creative professional with a passion for innovative design and user experience.'
        }
      },
      3: {
        personal: {
          ...resumeData.personal,
          summary: 'Strategic leader with proven track record of driving organizational growth and transformation.'
        }
      }
    };

    if (templates[templateId]) {
      setResumeData(prev => ({
        ...prev,
        ...templates[templateId]
      }));
    }

    setCurrentPage('builder');
  };

  const applyToJob = (jobIndex) => {
    const jobMatches = [
      { company: 'Google', role: 'Senior Software Engineer' },
      { company: 'Meta', role: 'Full Stack Engineer' },
      { company: 'Netflix', role: 'Platform Engineer' },
      { company: 'Airbnb', role: 'Technical Lead' },
      { company: 'Stripe', role: 'Senior Engineer' }
    ];

    const job = jobMatches[jobIndex];
    if (job) {
      setTimeout(() => {
        alert(`Application submitted successfully!\n\nPosition: ${job.role}\nCompany: ${job.company}\nYour resume score: ${resumeScore}%\n\nYou'll hear back within 1-2 weeks.`);
      }, 1000);
    }
  };

  const startInterview = () => {
    const questions = [
      "Tell me about yourself and your background.",
      "Describe a challenging project you've worked on.",
      "How do you handle tight deadlines and pressure?",
      "What's your experience with team leadership?",
      "Where do you see yourself in 5 years?"
    ];

    const randomQuestion = questions[Math.floor(Math.random() * questions.length)];

    setChatMessages(prev => [...prev, {
      type: 'ai',
      specialist: 'interview',
      content: `Let's practice! Here's a common interview question: "${randomQuestion}" Take your time to think and respond when ready.`,
      timestamp: new Date()
    }]);

    setSelectedAI('interview');
    setAiChatOpen(true);
  };

  const analyzeResume = () => {
    setIsAnalyzing(true);

    setTimeout(() => {
      const newScore = Math.min(95, resumeScore + Math.floor(Math.random() * 10));
      setResumeScore(newScore);
      setIsAnalyzing(false);
    }, 3000);
  };

  const suggestions = [
    {
      type: 'improvement',
      title: 'Add More Metrics',
      description: 'Include quantifiable achievements. Use numbers to show impact.',
      priority: 'high'
    },
    {
      type: 'keyword',
      title: 'Missing Keywords',
      description: 'Add "microservices", "scalability", and "GraphQL" to match job requirements.',
      priority: 'medium'
    },
    {
      type: 'format',
      title: 'Section Organization',
      description: 'Consider moving Skills section above Experience for better visibility.',
      priority: 'low'
    }
  ];

  const updateResumeData = (section, field, value) => {
    setResumeData(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value
      }
    }));
  };

  return (
    <div className={`min-h-screen transition-all duration-300 ${
      darkMode 
        ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white' 
        : 'bg-gradient-to-br from-blue-50 via-white to-purple-50 text-gray-900'
    } ${dyslexiaMode ? 'font-mono' : 'font-sans'}`}>

      {/* Navigation */}
      <nav className={`fixed top-0 left-0 right-0 z-50 ${darkMode ? 'bg-gray-900/90' : 'bg-white/90'} backdrop-blur-lg border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
        <div className="max-w-6xl mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <Rocket className="w-5 h-5 text-white" />
              </div>
              <span className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                LaunchpadPoint
              </span>
            </div>

            <div className="flex items-center space-x-1">
              {['builder', 'analytics', 'templates', 'career'].map(page => (
                <button
                  key={page}
                  onClick={() => setCurrentPage(page)}
                  className={`px-4 py-2 rounded-lg font-medium transition-all capitalize ${
                    currentPage === page
                      ? 'bg-blue-500 text-white shadow-lg'
                      : darkMode
                      ? 'text-gray-300 hover:text-white hover:bg-gray-700'
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  {page}
                </button>
              ))}
            </div>

            <div className="flex items-center space-x-2">
              <button
                onClick={() => setDyslexiaMode(!dyslexiaMode)}
                className={`p-2 rounded-lg transition-all ${
                  dyslexiaMode
                    ? 'bg-cyan-500 text-white'
                    : darkMode
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                <Type className="w-5 h-5" />
              </button>
              <button
                onClick={() => setDarkMode(!darkMode)}
                className={`p-2 rounded-lg transition-all ${
                  darkMode
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                {darkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
              </button>
              <button 
                onClick={exportResume}
                className="px-4 py-2 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-lg hover:shadow-lg transition-all flex items-center space-x-2"
              >
                <Download className="w-4 h-4" />
                <span>Export</span>
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* AI Chat Sidebar */}
      <div className={`fixed right-0 top-16 bottom-0 w-80 ${darkMode ? 'bg-gray-900' : 'bg-white'} border-l ${darkMode ? 'border-gray-700' : 'border-gray-200'} transform transition-transform z-40 ${
        aiChatOpen ? 'translate-x-0' : 'translate-x-full'
      }`}>
        <div className="p-4 border-b border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">AI Specialists</h3>
            <button
              onClick={() => setAiChatOpen(false)}
              className="p-1 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800"
            >
              ✕
            </button>
          </div>

          <div className="grid grid-cols-2 gap-2">
            {Object.entries(aiSpecialists).map(([key, ai]) => {
              const IconComponent = ai.icon;
              return (
                <button
                  key={key}
                  onClick={() => setSelectedAI(key)}
                  className={`p-3 rounded-lg text-left transition-all ${
                    selectedAI === key
                      ? `bg-gradient-to-r ${ai.color} text-white shadow-lg`
                      : darkMode
                      ? 'bg-gray-800 hover:bg-gray-700'
                      : 'bg-gray-50 hover:bg-gray-100'
                  }`}
                >
                  <IconComponent className="w-5 h-5 mb-1" />
                  <div className="text-sm font-medium">{ai.name}</div>
                </button>
              );
            })}
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4 space-y-4 max-h-96">
          {chatMessages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : darkMode
                    ? 'bg-gray-800 text-gray-200'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                {message.type === 'ai' && (
                  <div className="text-xs opacity-70 mb-1">
                    {aiSpecialists[message.specialist].name}
                  </div>
                )}
                <div className="text-sm">{message.content}</div>
              </div>
            </div>
          ))}
        </div>

        <div className="p-4 border-t border-gray-200 dark:border-gray-700">
          <div className="flex space-x-2">
            <input
              type="text"
              value={currentMessage}
              onChange={(e) => setCurrentMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              placeholder={`Ask ${aiSpecialists[selectedAI].name}...`}
              className={`flex-1 px-3 py-2 rounded-lg border ${
                darkMode
                  ? 'bg-gray-800 border-gray-600 text-white'
                  : 'bg-white border-gray-300'
              } focus:outline-none focus:ring-2 focus:ring-blue-500`}
            />
            <button
              onClick={sendMessage}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
            >
              <MessageSquare className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <main className={`pt-20 pb-8 px-4 max-w-6xl mx-auto transition-all ${aiChatOpen ? 'mr-80' : ''}`}>

        {/* Builder Page */}
        {currentPage === 'builder' && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Form Section */}
            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-xl shadow-lg p-6`}>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold flex items-center space-x-3">
                  <Edit3 className="w-6 h-6" />
                  <span>Build Your Resume</span>
                </h2>
                <button
                  onClick={() => setAiChatOpen(true)}
                  className="px-4 py-2 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:shadow-lg transition-all flex items-center space-x-2"
                >
                  <Sparkles className="w-4 h-4" />
                  <span>AI Help</span>
                </button>
              </div>

              {/* Tabs */}
              <div className="flex space-x-1 mb-6 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
                {['personal', 'experience', 'education', 'skills'].map(tab => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`flex-1 py-2 px-4 rounded-md transition-all capitalize ${
                      activeTab === tab
                        ? 'bg-white dark:bg-gray-600 shadow-sm'
                        : 'hover:bg-white/50 dark:hover:bg-gray-600/50'
                    }`}
                  >
                    {tab}
                  </button>
                ))}
              </div>

              {/* Personal Tab */}
              {activeTab === 'personal' && (
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Full Name</label>
                    <input
                      type="text"
                      value={resumeData.personal.fullName}
                      onChange={(e) => updateResumeData('personal', 'fullName', e.target.value)}
                      className={`w-full px-4 py-3 rounded-lg border ${
                        darkMode
                          ? 'bg-gray-700 border-gray-600 text-white'
                          : 'bg-white border-gray-300'
                      } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Professional Title</label>
                    <input
                      type="text"
                      value={resumeData.personal.jobTitle}
                      onChange={(e) => updateResumeData('personal', 'jobTitle', e.target.value)}
                      className={`w-full px-4 py-3 rounded-lg border ${
                        darkMode
                          ? 'bg-gray-700 border-gray-600 text-white'
                          : 'bg-white border-gray-300'
                      } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    />
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium mb-2">Email</label>
                      <input
                        type="email"
                        value={resumeData.personal.email}
                        onChange={(e) => updateResumeData('personal', 'email', e.target.value)}
                        className={`w-full px-4 py-3 rounded-lg border ${
                          darkMode
                            ? 'bg-gray-700 border-gray-600 text-white'
                            : 'bg-white border-gray-300'
                        } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium mb-2">Phone</label>
                      <input
                        type="tel"
                        value={resumeData.personal.phone}
                        onChange={(e) => updateResumeData('personal', 'phone', e.target.value)}
                        className={`w-full px-4 py-3 rounded-lg border ${
                          darkMode
                            ? 'bg-gray-700 border-gray-600 text-white'
                            : 'bg-white border-gray-300'
                        } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                      />
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Location</label>
                    <input
                      type="text"
                      value={resumeData.personal.location}
                      onChange={(e) => updateResumeData('personal', 'location', e.target.value)}
                      className={`w-full px-4 py-3 rounded-lg border ${
                        darkMode
                          ? 'bg-gray-700 border-gray-600 text-white'
                          : 'bg-white border-gray-300'
                      } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Professional Summary</label>
                    <textarea
                      rows={4}
                      value={resumeData.personal.summary}
                      onChange={(e) => updateResumeData('personal', 'summary', e.target.value)}
                      className={`w-full px-4 py-3 rounded-lg border ${
                        darkMode
                          ? 'bg-gray-700 border-gray-600 text-white'
                          : 'bg-white border-gray-300'
                      } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    />
                  </div>
                </div>
              )}

              {/* Skills Tab */}
              {activeTab === 'skills' && (
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Skills (comma separated)</label>
                    <textarea
                      rows={4}
                      value={resumeData.skills.join(', ')}
                      onChange={(e) => setResumeData(prev => ({
                        ...prev,
                        skills: e.target.value.split(',').map(s => s.trim()).filter(s => s)
                      }))}
                      className={`w-full px-4 py-3 rounded-lg border ${
                        darkMode
                          ? 'bg-gray-700 border-gray-600 text-white'
                          : 'bg-white border-gray-300'
                      } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Job Description (for AI analysis)</label>
                    <textarea
                      rows={6}
                      value={resumeData.jobDescription}
                      onChange={(e) => setResumeData(prev => ({
                        ...prev,
                        jobDescription: e.target.value
                      }))}
                      placeholder="Paste the job description you're applying for..."
                      className={`w-full px-4 py-3 rounded-lg border ${
                        darkMode
                          ? 'bg-gray-700 border-gray-600 text-white'
                          : 'bg-white border-gray-300'
                      } focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    />
                  </div>
                  <button
                    onClick={analyzeResume}
                    disabled={isAnalyzing}
                    className="w-full py-3 bg-gradient-to-r from-green-500 to-emerald-500 text-white rounded-lg hover:shadow-lg transition-all flex items-center justify-center space-x-2 disabled:opacity-50"
                  >
                    {isAnalyzing ? (
                      <>
                        <RefreshCw className="w-5 h-5 animate-spin" />
                        <span>Analyzing...</span>
                      </>
                    ) : (
                      <>
                        <Zap className="w-5 h-5" />
                        <span>Analyze with AI</span>
                      </>
                    )}
                  </button>
                </div>
              )}
            </div>

            {/* Preview Section */}
            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-xl shadow-lg p-6 sticky top-24 max-h-screen overflow-y-auto`}>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold flex items-center space-x-3">
                  <Eye className="w-6 h-6" />
                  <span>Live Preview</span>
                </h2>
                <div className="flex items-center space-x-2">
                  <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                    resumeScore >= 90 ? 'bg-green-100 text-green-800' :
                    resumeScore >= 80 ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {resumeScore}% Match
                  </div>
                </div>
              </div>

              {/* Resume Preview */}
              <div className={`${darkMode ? 'bg-gray-900' : 'bg-gray-50'} p-6 rounded-lg mb-6`}>
                <div className="text-center mb-6">
                  <h2 className="text-2xl font-bold mb-2">{resumeData.personal.fullName}</h2>
                  <p className="text-lg text-blue-600 mb-2">{resumeData.personal.jobTitle}</p>
                  <p className="text-sm text-gray-600">
                    {resumeData.personal.email} • {resumeData.personal.phone} • {resumeData.personal.location}
                  </p>
                </div>

                <div className="space-y-6">
                  <div>
                    <h3 className="text-lg font-semibold mb-3 pb-2 border-b border-gray-300">Summary</h3>
                    <p className="text-sm leading-relaxed">{resumeData.personal.summary}</p>
                  </div>

                  <div>
                    <h3 className="text-lg font-semibold mb-3 pb-2 border-b border-gray-300">Skills</h3>
                    <div className="flex flex-wrap gap-2">
                      {resumeData.skills.map((skill, index) => (
                        <span
                          key={index}
                          className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* AI Suggestions */}
              <div className={`${darkMode ? 'bg-blue-900/20' : 'bg-blue-50'} rounded-lg p-4`}>
                <h3 className="font-semibold mb-3 flex items-center space-x-2">
                  <Lightbulb className="w-5 h-5" />
                  <span>AI Suggestions</span>
                </h3>
                <div className="space-y-3">
                  {suggestions.map((suggestion, index) => (
                    <div
                      key={index}
                      className={`p-3 rounded-lg ${darkMode ? 'bg-gray-800' : 'bg-white'} border-l-4 ${
                        suggestion.priority === 'high' ? 'border-red-500' :
                        suggestion.priority === 'medium' ? 'border-yellow-500' :
                        'border-green-500'
                      }`}
                    >
                      <div className="flex items-start space-x-2">
                        {suggestion.priority === 'high' ? (
                          <AlertCircle className="w-4 h-4 text-red-500 mt-0.5" />
                        ) : suggestion.priority === 'medium' ? (
                          <AlertCircle className="w-4 h-4 text-yellow-500 mt-0.5" />
                        ) : (
                          <CheckCircle className="w-4 h-4 text-green-500 mt-0.5" />
                        )}
                        <div>
                          <h4 className="font-medium text-sm">{suggestion.title}</h4>
                          <p className="text-xs text-gray-600 dark:text-gray-400">{suggestion.description}</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Analytics Page */}
        {currentPage === 'analytics' && (
          <div className="space-y-8">
            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-xl shadow-lg p-6`}>
              <h2 className="text-2xl font-bold mb-6 flex items-center space-x-3">
                <BarChart3 className="w-6 h-6" />
                <span>Resume Analytics</span>
              </h2>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                <div className={`${darkMode ? 'bg-gray-700' : 'bg-gradient-to-br from-blue-50 to-blue-100'} p-6 rounded-lg`}>
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">Overall Score</p>
                      <p className="text-3xl font-bold text-blue-600">{resumeScore}%</p>
                    </div>
                    <Target className="w-8 h-8 text-blue-500" />
                  </div>
                </div>

                <div className={`${darkMode ? 'bg-gray-700' : 'bg-gradient-to-br from-green-50 to-green-100'} p-6 rounded-lg`}>
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">Job Matches</p>
                      <p className="text-3xl font-bold text-green-600">247</p>
                    </div>
                    <Briefcase className="w-8 h-8 text-green-500" />
                  </div>
                </div>

                <div className={`${darkMode ? 'bg-gray-700' : 'bg-gradient-to-br from-purple-50 to-purple-100'} p-6 rounded-lg`}>
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">Market Ranking</p>
                      <p className="text-3xl font-bold text-purple-600">Top 15%</p>
                    </div>
                    <TrendingUp className="w-8 h-8 text-purple-500" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Templates Page */}
        {currentPage === 'templates' && (
          <div className="space-y-8">
            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-xl shadow-lg p-6`}>
              <h2 className="text-2xl font-bold mb-6 flex items-center space-x-3">
                <FileText className="w-6 h-6" />
                <span>Resume Templates</span>
              </h2>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[
                  { id: 1, name: 'Modern Professional', color: 'from-blue-500 to-cyan-500', preview: 'Clean, modern design perfect for tech roles' },
                  { id: 2, name: 'Creative Designer', color: 'from-purple-500 to-pink-500', preview: 'Bold, artistic layout for creative professionals' },
                  { id: 3, name: 'Executive Classic', color: 'from-gray-600 to-gray-800', preview: 'Traditional, elegant format for senior positions' }
                ].map(template => (
                  <div key={template.id} className={`${darkMode ? 'bg-gray-700' : 'bg-gray-50'} rounded-lg p-6 hover:shadow-lg transition-all cursor-pointer group`}>
                    <div className={`w-full h-32 bg-gradient-to-br ${template.color} rounded-lg mb-4 flex items-center justify-center group-hover:scale-105 transition-transform`}>
                      <FileText className="w-12 h-12 text-white opacity-80" />
                    </div>
                    <h3 className="font-semibold mb-2">{template.name}</h3>
                    <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">{template.preview}</p>
                    <button
                      onClick={() => applyTemplate(template.id)}
                      className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors">
                      Use Template
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Career Page */}
        {currentPage === 'career' && (
          <div className="space-y-8">
            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-xl shadow-lg p-6`}>
              <h2 className="text-2xl font-bold mb-6 flex items-center space-x-3">
                <Briefcase className="w-6 h-6" />
                <span>Career Opportunities</span>
              </h2>

              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                <div className="lg:col-span-2">
                  <h3 className="text-lg font-semibold mb-4">Job Matches</h3>
                  <div className="space-y-4">
                    {[
                      { company: 'Google', role: 'Senior Software Engineer', match: 95, salary: '$180k-220k', location: 'Mountain View, CA' },
                      { company: 'Meta', role: 'Full Stack Engineer', match: 92, salary: '$170k-210k', location: 'Menlo Park, CA' },
                      { company: 'Netflix', role: 'Platform Engineer', match: 88, salary: '$190k-240k', location: 'Los Gatos, CA' }
                    ].map((job, index) => (
                      <div key={index} className={`${darkMode ? 'bg-gray-700' : 'bg-gray-50'} rounded-lg p-4 hover:shadow-md transition-all`}>
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <div className="flex items-center space-x-3 mb-2">
                              <h4 className="font-semibold">{job.role}</h4>
                              <span className={`px-2 py-1 rounded-full text-xs ${
                                job.match >= 90 ? 'bg-green-100 text-green-800' :
                                job.match >= 80 ? 'bg-yellow-100 text-yellow-800' :
                                'bg-red-100 text-red-800'
                              }`}>
                                {job.match}% match
                              </span>
                            </div>
                            <p className="text-blue-600 font-medium mb-1">{job.company}</p>
                            <p className="text-sm text-gray-600 dark:text-gray-400">{job.location}</p>
                            <p className="text-sm font-medium text-green-600">{job.salary}</p>
                          </div>
                          <button 
                            onClick={() => applyToJob(index)}
                            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors">
                            Apply
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="space-y-6">
                  <div className={`${darkMode ? 'bg-gray-700' : 'bg-gray-50'} rounded-lg p-4`}>
                    <h3 className="font-semibold mb-3 flex items-center space-x-2">
                      <Star className="w-5 h-5 text-yellow-500" />
                      <span>Interview Tips</span>
                    </h3>
                    <ul className="text-sm space-y-2 mb-4">
                      <li>• Practice system design questions</li>
                      <li>• Prepare STAR method examples</li>
                      <li>• Research company culture</li>
                      <li>• Review your past projects</li>
                    </ul>
                    <button 
                      onClick={startInterview}
                      className="w-full py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors">
                      Start Practice Interview
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

      </main>

      {/* Floating AI Button */}
      {!aiChatOpen && (
        <button
          onClick={() => setAiChatOpen(true)}
          className="fixed bottom-6 right-6 w-14 h-14 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-full shadow-lg hover:shadow-xl transition-all flex items-center justify-center z-50 hover:scale-110"
        >
          <Sparkles className="w-6 h-6" />
        </button>
      )}
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <ResumeBuilder />
    </div>
  );
}

export default App;
